#!/bin/sh
java -Dunixfs=false -DuseExtendedFileAttributes=false -Dsun.net.client.defaultConnectTimeout=10000 -Dsun.net.client.defaultReadTimeout=60000 -Dapplication.deployment=ipkg -Dapplication.analytics=true -Dapplication.dir=/opt/usr/share/filebot/data -Djava.io.tmpdir=/opt/usr/share/filebot/temp -Djna.library.path=/opt/usr/share/filebot -Djava.library.path=/opt/usr/share/filebot -jar /opt/usr/share/filebot/FileBot.jar "$@"